import Layout from '../components/MyLayout';
import Link from 'next/link';
import fetch from 'isomorphic-unfetch';

const Index = props => (
  <Layout>
    <h1>Rick and Morty Episodes</h1>
    <ul>
      {props.episodes.map(episode => (
        <li key={episode.id}>
          <Link href="/p/[id]" as={`/p/${episode.id}`}>
            <a>{episode.name}</a>
          </Link>
        </li>
      ))}
    </ul>
  </Layout>
);

