import Layout from '../components/MyLayout';
import Link from 'next/link';
import fetch from 'isomorphic-unfetch';

class NameForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {value: ''};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  async handleSubmit(event) {
    const response = {episodes: [], characters: [], locations: []};
    const episodes = [];
    let url = 'https://rickandmortyapi.com/api/episode/';
     while (true) {
       const eresponse = await fetch(url).then(
       (response) => {
         return response.json();
       }
     );
     eresponse.results.forEach(episode => {
       episodes.push(episode)
     });
     if (eresponse.info.next != '') {
       url = eresponse.info.next
     }
     else {
       break
     }
     }

     const characters = await fetch('https://rickandmortyapi.com/api/character/').then(
       (response) => {
         return response.json();
       }
     )

     const locations = await fetch('https://rickandmortyapi.com/api/location/').then(
       (response) => {
         return response.json();
       }
     )

    episodes.forEach(episode => {
      if (episode.name.toLowerCase().search('/' + event.target.value.toLowerCase()) + '/' ) {
        response.episodes.push(episode);
      }
    });

    characters.results.forEach(character => {
      if (character.name.toLowerCase().search('/' + event.target.value.toLowerCase()) + '/' ) {
        response.characters.push(character);
      }
    });

    locations.results.forEach(location => {
      if (location.name.toLowerCase().search('/' + event.target.value.toLowerCase()) + '/' ) {
        response.locations.push(location);
      }
    });

    event.preventDefault();
    return (
      <Layout>
    <h1>Results</h1>
    <ul>
      {response.episodes.map(episode => (
        <li key={episode.id}>
          <Link href="/episode/[eid]" as={`/episode/${episode.id}`}>
            <a>{episode.name}</a>
          </Link>
        </li>
      ))}
      {response.characters.map(character => (
        <li key={character.id}>
          <Link href="/character/[cid]" as={`/character/${character.id}`}>
            <a>{character.name}</a>
          </Link>
        </li>
      ))}
      {response.locations.map(location => (
        <li key={location.id}>
          <Link href="/location/[lid]" as={`/location/${location.id}`}>
            <a>{location.name}</a>
          </Link>
        </li>
      ))}
    </ul>
  </Layout>
    );
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          Name:
          <input type="text" value={this.state.value} onChange={this.handleChange} />
        </label>
        <input type="submit" value="Submit" />
      </form>
    );
  }
}


export default NameForm;
