import Layout from '../../components/MyLayout';
import fetch from 'isomorphic-unfetch';
import Link from 'next/link';

const Episode = props => (
  <Layout>
    <h1>{props.episode.name}</h1>
<p>Release date: {props.episode.air_date}</p>
    <p>Episode: {props.episode.episode}</p>
    {props.episode.characters.map(character => (
        <li key={character.id}>
          <Link href="/p/[cid]" as={`/p/${character.id}`}>
            <a>{character.name}</a>
          </Link>
        </li>
      ))}
  </Layout>
);

Episode.getInitialProps = async function(context) {
  const { eid } = context.query;

  const characters_arr = [];
  const res = await fetch(`https://rickandmortyapi.com/api/episode/${eid}`).then(
    (response) => {
      return response.json();
    }
  )

  for (let i = 0; i < res.characters.length; i++) {
    const character = res.characters[i];
    const crest = await fetch(character).then(
      (response) => {
        return response.json();
      })
    characters_arr.push(crest);
  };

  console.log(characters_arr);

  return { episode: res, characters: characters_arr };
};

export default Episode;
