import Layout from '../../components/MyLayout';
import fetch from 'isomorphic-unfetch';
import Link from 'next/link';

const Character = props => (
  <Layout>
    <h1>{props.character.name}</h1>
    {props.character.image ? <img src={props.character.image} /> : null}
<p>Status: {props.character.status}</p>
    <p>Specie: {props.character.species}</p>
<p>{props.character.type ? `Type: ${props.character.type}`: null}</p>
<p>Gender: {props.character.gender}</p>
<p>Origin: {<Link href="/location/[lid]" as={`/location/${props.origin.id}`}>
            <a>{props.character.origin.name}</a>
          </Link>}</p>
<p>Location: {<Link href="/location/[lid]" as={`/location/${props.location.id}`}>
            <a>{props.character.location.name}</a>
          </Link>}</p>
      <h2>Episodes</h2>
      {props.episodes.map(episode => (
        <li key={episode.id}>
          <Link href="/episode/[eid]" as={`/eepisode/${episode.id}`}>
            <a>{episode.name}</a>
          </Link>
        </li>
      ))}
  </Layout>
);

Character.getInitialProps = async function(context) {
  const { cid } = context.query;


  const episodes_arr = [];
  const res = await fetch(`https://rickandmortyapi.com/api/character/${cid}`).then(
    (response) => {
      return response.json();
    }
  )
  const origin_res = await fetch(`https://rickandmortyapi.com/api/location/${res.origin.url}`).then(
    (response) => {
      return response.json();
    }
  )
  const location_res = await fetch(`https://rickandmortyapi.com/api/location/${res.location.url}`).then(
    (response) => {
      return response.json();
    }
  )
  for (let i = 0; i < res.episode.length; i++) {
    const episode = res.episode[i];
    const crest = await fetch(episode).then(
      (response) => {
        return response.json();
      })
    episodes_arr.push(crest);
  };



  return { character: res, episodes: episodes_arr, origin: origin_res, location: location_res };
};

export default Character;
