import Layout from '../../components/MyLayout';
import Link from 'next/link';
const Results = props => (
<Layout>
    <h1>Results</h1>
    <ul>
      {props.results.episodes.map(episode => (
        <li key={episode.id}>
          <Link href="/episode/[eid]" as={`/episode/${episode.id}`}>
            <a>{episode.name}</a>
          </Link>
        </li>
      ))}
      {props.results.characters.map(character => (
        <li key={character.id}>
          <Link href="/character/[cid]" as={`/character/${character.id}`}>
            <a>{character.name}</a>
          </Link>
        </li>
      ))}
      {props.results.locations.map(location => (
        <li key={location.id}>
          <Link href="/location/[lid]" as={`/location/${location.id}`}>
            <a>{location.name}</a>
          </Link>
        </li>
      ))}
    </ul>
  </Layout>
);

Results.getInitialProps = async function(context) {
  const { query } = context.query;
  const response = {episodes: [], characters: [], locations: []};
  let eurl = 'https://rickandmortyapi.com/api/episode/';
   while (true) {
     const eresponse = await fetch(eurl).then(
     (response) => {
       return response.json();
     }
   );
   eresponse.results.forEach(episode => {
    if (episode.name.toLowerCase().search('/' + event.target.value.toLowerCase()) + '/' ) {
      response.episodes.push(episode);
    }
   });
   if (eresponse.info.next != '') {
     eurl = eresponse.info.next
   }
   else {
     break
   }
   }

   let curl = 'https://rickandmortyapi.com/api/character/';
   while (true) {
     const cresponse = await fetch(curl).then(
     (response) => {
       return response.json();
     }
   );
   cresponse.results.forEach(character => {
    if (character.name.toLowerCase().search('/' + event.target.value.toLowerCase()) + '/' ) {
      response.characters.push(character);
    }
   });
   if (cresponse.info.next != '') {
     curl = cresponse.info.next
   }
   else {
     break
   }
   }

   let lurl = 'https://rickandmortyapi.com/api/location/';
   while (true) {
     const lresponse = await fetch(lurl).then(
     (response) => {
       return response.json();
     }
   );
   lresponse.results.forEach(location => {
    if (location.name.toLowerCase().search('/' + event.target.value.toLowerCase()) + '/' ) {
      response.locations.push(location);
    }
   });
   if (lresponse.info.next != '') {
     lurl = lresponse.info.next
   }
   else {
     break
   }



  return {results: response};
}}

export default Results;
