import Layout from '../components/MyLayout';
import Link from 'next/link';
import fetch from 'isomorphic-unfetch';

const Index = props => (
  <Layout>
    <h1>Rick and Morty Episodes</h1>
    <ul>
      {props.episodes.map(episode => (
        <li key={episode.id}>
          <Link href="/p/[id]" as={`/p/${episode.id}`}>
            <a>{episode.name}</a>
          </Link>
        </li>
      ))}
    </ul>
  </Layout>
);

Index.getInitialProps = async function() {

  const eresponse = await fetch('https://rickandmortyapi.com/api/episode').then(
    (response) => {
      return response.json();
    }
  );

  const pages = eresponse.info.pages;
  const arr = []
  for (let i = 0; i < pages; i++) {
    const ep = await fetch(`https://rickandmortyapi.com/api/episode/$page=${i+1}`).then(
      (response) => {
        return response.json();
      }
    )
    arr.concat(ep.results)
  }
  console.log(arr);
  // console.log(`episode data fetched. Count: ${data}`);

  return {
    episodes: arr
  };
};

export default Index;
